<script>
import axios from 'axios'
import activityTimeout from '../user/activityTimeout.vue';
import store from '../../store/store'

export default {
  components: { activityTimeout },
	name: "Navbar",
    data () {
        return {
            showNavBar: false,
			userMenu: false,
			rol: " ",
			username: " "
        };
    },
	methods: {
		switchVisibility() {
			this.showNavBar = true;
		},
		switchVisibilityUserMenu(){
			this.userMenu = true;
		}, close(){
			localStorage.clear();
			this.$store.commit("setAuthentication",false);
			this.$store.commit("setRolAuthenticate","");
            this.$router.push("/");
			let json = {
				"usuario_log" : this.username,
				"tipo" : "FIN_SESION"
			};
			axios.post('http://127.0.0.1:8080/insertLog',json, {headers: {
			"Access-Control-Allow-Origin": "*",
			"Access-Control-Allow-Methods": "GET, POST, PATCH, PUT, DELETE, OPTIONS",
			"Access-Control-Allow-Headers": "Origin, Content-Type, X-Auth-Token",
			withCredentials: true}
			});
		}
	},
	created() {
      window.addEventListener('click', (e) => {
        if (!this.$el.contains(e.target)){
          	this.showNavBar = false;
			this.userMenu = false;
			this.showDropDownMenu = false;
        }
      })
  	}, mounted(){
		this.username = store.state.username;
		this.rol = store.state.rol;
	  }
}
</script>

